import React, { Component } from 'react';
import { StyleSheet, View, TextInput, Button } from 'react-native';

class Form extends Component {

  state = {
    rname:"",
    review:""
  }

  onChange = (e)=>{
    this.setState({[e.target.name]:e.target.value})
    console.log(e.target.name)
  }
    onPress = () => {
        console.log("pressed")
    }
    render(){
        return (
            <View style={styles.form}>
                <TextInput
                    style={styles.input}
                    placeholder='Name'
                    onChangeText={this.onChange}
                    name="rname"
                />
                <TextInput
                    style={styles.input}
                    placeholder='Review'
                    onChangeText={this.onChange}
                    name="review"
                />
                <View style={styles.btn}>
                <Button
                    onPress={this.onPress}
                    title='Add Review'
                    color='coral'
                />
                </View>
            </View>
        );
    }

}

const styles = StyleSheet.create({
  form: {
    width: "100%",
    height: 150,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#eee",
    // marginTop: 2
  },
  input: {
    width: '70%',
    height: 38,
    padding: 5,
    borderBottomWidth: 1.5,
    borderBottomColor: "#ddd",
    marginBottom: 5
  },
  btn: {
    width: "70%",
    height: 35,
    marginTop: 5,
    // padding: 2,
  }
});

export default Form;
