import React, { Component } from 'react';
import { StyleSheet, Text, View, TextInput, Button } from 'react-native';

class Reviews extends Component {
  render(){
    return (
            <View style={styles.form}>
                <TextInput
                    style={styles.input}
                    placeholder='Name'
                    // onChange={this.onChange}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Review'
                    // onChange={this.onChange}
                />
                <Button
                    // onPress={this.onPress}
                    style={styles.button}
                    title='Add Review'
                    color='coral'
                />
            </View>
    );
  }

}

const styles = StyleSheet.create({
  form: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#eee",
    marginTop: 2
  },
  input: {
    width: '25%',
    height: 35,
    padding: 5,
    border: 'solid',
    margin: 5
  }
});

export default Reviews;
